15.3 자동화 스크립트 활용

15.3.1 Google Apps Script 함수
/**
 * 파일명 자동 생성
 */
function generateFileName(mainCode, subCode, description, version, status) {
  const today = new Date();
  const dateStr = `${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`;

  return `${mainCode}_${subCode}_${description}_v${version}_${dateStr}_${status}`;
}

/**
 * 사용 예시
 */
const fileName = generateFileName('SOURCING', 'PO', 'Aqara_M2Hub_발주서', '1.0', 'ISSUED');
// 결과: SOURCING_PO_Aqara_M2Hub_발주서_v1.0_2024-10-20_ISSUED

